// c4ef0e54c94849e194ad5251b2d39e56
function formatDate(date) {
    let d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

    return [year, month, day].join('-');
}

class NewsApi {
    constructor({ newsUrl, headers }) {
        this.newsUrl = newsUrl
        this.headers = headers
    }

    _checkResponse(res) {
        return res.ok ? res.json() : Promise.reject(`Error: ${res.status}`)
    }

    getCardsList() {
        return fetch(`${this.newsUrl}/everything?q=bitcoin&apiKey=c4ef0e54c94849e194ad5251b2d39e56`,
            {
                method: 'GET',
                headers: this.headers
            })
            .then(this._checkResponse)
            .then((res) => { console.log(res, formatDate()) })
    }
}

const newsApi = new NewsApi({
    newsUrl: 'https://nomoreparties.co/news/v2/',
    headers: {
        'Content-Type': 'application/json'
    }
})

export default newsApi;
