import React, { useState } from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm.js';
import UseFormValidation from '../FormValidation/FormValidation.js';

function Authentication(props) {
    const [values, setValues] = useState({});
const [emailValid, setEmailValid] = useState(false);
const [passwordValid, setPasswordValid] = useState(false);
    const handleChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        setValues({[name]: value});
    };
    return (
        <PopupWithForm
            altFormTitle={props.altFormTitle}
            formSubmit={props.formSubmit}
            buttonText='Sign in'
            title='Sign in'
            type='auth'
            isOpen={props.isOpen}
            openAltForm={props.openAltForm}
            altFormLink={props.altFormLink}
            onClose={props.onClose}
        >
            <h6 className='popup__input-title popup__input-title_type_email'>Email</h6>
            <input minLength='1'
                maxLength='30'
                type='email'
                className='popup__input popup__input_type_email'
                placeholder='Email'
                onChange={handleChange} />
            <span className='popup__error'>Ivalid email address</span>

            <h6 className='popup__input-title popup__input-title_type_password'>Password</h6>
            <input minLength='1'
                maxLength='30'
                type='text'
                className='popup__input popup__input_type_password'
                placeholder='Enter password'
                onChange={handleChange} />
        </PopupWithForm>
    )
}

export default Authentication;
