const validate = (value) => {
    //Email errors
   const errors = {};
   if (!value.email) {
       errors.email = 'Check Email';
   } else if (
       !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(value.email)
   ) {
       errors.email = 'Invalid email address';
   }
   //Password Errors
   if(!value.password  || value.password.length<6){
       errors.password = 'Check Password'
   }
   return errors;
 }
 export default validate;
