import React from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function Navigation(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const history = useHistory();

    function signOut() {
        localStorage.removeItem('token');
        history.push('/');
    }

    return (
        <nav className={`nav nav_page_${props.page} ${props.isNavMobileOpen ? 'nav_is_open' : 'nav_is_hidden'}`}>
            <div className={`nav__menu nav__menu_page_${props.page} ${props.isNavMobileOpen ? 'nav__menu_is_open' : ''}`}>
                <button className={`button nav__close nav__close_page_${props.page}`} onClick={props.onClose}>+</button>
                <NavLink to='/' className={`link nav__home nav__home_page_${props.page}`}
                    exact activeClassName='nav__home_active'>Home</NavLink>
                {props.page === 'saved-news' && <NavLink to='/saved-news'
                    className='button nav__saved-articles'
                    exact activeClassName='nav__saved-articles_active'>Saved articles</NavLink>}
                {props.page === 'saved-news'
                    ? <button className='button nav__exit' onClick={signOut}>{currentUser.username}</button>
                    : <NavLink to='/signin' className='link nav__signin' onClick={props.onAuth}>Sign in</NavLink>
                }
            </div>
        </nav>
    )
}

export default Navigation;
