class NewsApi {
    constructor({ newsUrl, headers }) {
        this.newsUrl = newsUrl
        this.headers = headers
    }

    _checkResponse(res) {
        return res.ok ? console.log(res.json()) : Promise.reject(`Error: ${res.status}`)
    }

    getCardsList() {
        return fetch('https://nomoreparties.co/news/v2/top-headlines?country=us&apiKey=c4ef0e54c94849e194ad5251b2d39e56', {
            method: 'GET',
            headers: this.headers
        })
            .then(this._checkResponse)
    }
}

const newsApi = new NewsApi({
    newsUrl: 'https://newsapi.org/v2/',
    headers: {
        'Content-Type': 'application/json'
    }
})

export default newsApi;
