// c4ef0e54c94849e194ad5251b2d39e56
class NewsApi {
    constructor({ newsUrl, headers }) {
        this.newsUrl = newsUrl
        this.headers = headers
    }

    date = new Date().toString();

    _checkResponse(res) {
        return res.ok ? res.json() : Promise.reject(`Error: ${res.status}`)
    }

    getCardsList() {
        return fetch(`${this.newsUrl}/everything?q=bitcoin?&from=2020-10-4&to=$date&apiKey=c4ef0e54c94849e194ad5251b2d39e56`,
            {
                method: 'GET',
                headers: this.headers
            })
            .then(this._checkResponse)
            .then((res) => { console.log(res) })
    }
}

const newsApi = new NewsApi({
    newsUrl: 'https://nomoreparties.co/news/v2/',
    headers: {
        'Content-Type': 'application/json'
    }
})

export default newsApi;
